mapper.select:
select count(t.communityId) from (select iv.community_id as communityId, sum(if(indicator_id='3.1.1.1',indicator_value,null)) as 3_1_1_1, sum(if(indicator_id='3.1.1.2',indicator_value,null)) as 3_1_1_2 from indicator_value iv left join community c on iv.community_id = c.community_id where c.city=#{city} and iv.report_year=#{reportYear}
and c.community_id=#{communityId}
and indicator_id like '3.1.1.%' group by iv.community_id) as t where t.3_1_1_1=0 or (t.3_1_1_1=1 and t.3_1_1_2=0)

select count(t.communityId) from (select iv.community_id as communityId, sum(if(indicator_id='3.1.2.1',indicator_value,null)) as 3_1_2_1, sum(if(indicator_id='3.1.2.2',indicator_value,null)) as 3_1_2_2 from indicator_value iv left join community c on iv.community_id = c.community_id where c.city=#{city} and iv.report_year=#{reportYear}
and c.community_id=#{communityId}
and indicator_id like '3.1.2.%' group by iv.community_id) as t where t.3_1_2_1=0 or (t.3_1_2_1=1 and t.3_1_2_2=0)

select count(t.communityId) from (select iv.community_id as communityId, sum(if(indicator_id='3.1.3.1',indicator_value,null)) as 3_1_3_1, sum(if(indicator_id='3.1.3.2',indicator_value,null)) as 3_1_3_2 from indicator_value iv left join community c on iv.community_id = c.community_id where c.city=#{city} and iv.report_year=#{reportYear}
and c.community_id=#{communityId}
and indicator_id like '3.1.3.%' group by iv.community_id) as t where t.3_1_3_1=0 or (t.3_1_3_1=1 and t.3_1_3_2=0)

select sum(indicator_value)  from indicator_value iv
       left join community c on iv.community_id = c.community_id
where c.city=#{city} and iv.report_year=#{reportYear}

            and c.community_id=#{communityId}

  and indicator_id='3.1.4.1'

