select count(t.community_id) from (select iv.community_id as community_id, sum(if(indicator_id='3.2.7.1',indicator_value,null)) as 3_2_7_1, sum(if(indicator_id='3.2.7.2',indicator_value,null)) as 3_2_7_2 from indicator_value iv left join community c on iv.community_id = c.community_id where c.city=#{city} and iv.report_year=#{reportYear}
and c.community_id=#{communityId}
group by community_id) as t where t.3_2_7_1 != '1' or t.3_2_7_2 !='1'

select sum(indicator_value)  from indicator_value iv
    left join community c on iv.community_id = c.community_id
where c.city=#{city} and iv.report_year=#{reportYear}

