select count(t.communityId) as num,county from (select iv.community_id as communityId, sum(if(indicator_id='3.1.1.1',indicator_value,null)) as 3_1_1_1, sum(if(indicator_id='3.1.1.2',indicator_value,null)) as 3_1_1_2, max(c.county) as county from indicator_value iv left join community c on iv.community_id = c.community_id where c.city=#{city}
and c.county=#{county}
and iv.report_year=#{reportYear} and indicator_id like '3.1.1.%' group by iv.community_id) as t where t.3_1_1_1=0 or (t.3_1_1_1=1 and t.3_1_1_2=0) group by county

select count(t.communityId) as num,county from (select iv.community_id as communityId, sum(if(indicator_id='3.1.2.1',indicator_value,null)) as 3_1_2_1, sum(if(indicator_id='3.1.2.2',indicator_value,null)) as 3_1_2_2, max(c.county) as county from indicator_value iv left join community c on iv.community_id = c.community_id where c.city=#{city}
and c.county=#{county}
and iv.report_year=#{reportYear} and indicator_id like '3.1.2.%' group by iv.community_id) as t where t.3_1_2_1=0 or (t.3_1_2_1=1 and t.3_1_2_2=0) group by county

