select sum(indicator_value) as num,c.street as street  from indicator_value iv
                                      left join community c on iv.community_id = c.community_id
where c.city=#{city}
and c.county=#{county}
  and iv.report_year=#{reportYear} and indicator_id='3.2.9.2' group by street

select c.community_id as communityId, max(c.county) as county, max(c.street) as street, max(c.community_name) as communityName, max(cb.area) as area, max(cb.number_court) as numberCourt, max(cb.number_people) as numberPeople, max(cb.number_family) as numberFamily from indicator_value iv left join community c on iv.community_id = c.community_id right join community_basic cb on c.community_id = cb.community_id where c.city=#{city} and iv.report_year=#{reportYear}
and c.county=#{county}
and c.street=#{street}
and c.community_id=#{communityId}
group by c.community_id
